var getTypoFreeVar = function(rootPathSteps, varName, typoList)
{

  //set root path
  var rootPath = '';
  for (const i in rootPathSteps){
    rootPath = rootPath + (i>0?'.':'') + rootPathSteps[i];
  }

  //get root object from AL or datalayer
  var flagObjFromAL = 1;
  var rootObj = _satellite.getVar(rootPath);
  if(rootObj === undefined){
    flagObjFromAL = 0;
    rootObj = adobeDataLayer.getState();
    for (const i in rootPathSteps){
      rootObj = rootObj[rootPathSteps[i]];
    }
  }

  //search for typo
  var typoValues = {};
  for (const key in typoList){
    if (rootObj[typoList[key]]){
      typoValues[typoList[key]] = rootObj[typoList[key]];
      if(flagObjFromAL) delete _satellite.getVar(rootPath)[typoList[key]];
    }
  }

  if(Object.keys(typoValues).length==0) return rootObj[varName];
  if(Object.keys(typoValues).length>1) _satellite.logger.warn("Warning! More than 1 typo detected for "+rootPath+'.'+varName);
  _satellite.logger.log("Typo fixed: use "+Object.keys(typoValues)[0]+'='+Object.values(typoValues)[0]+' to cover '+rootPath+'.'+varName)
  return Object.values(typoValues)[0];

}
_satellite.setVar('getTypoFreeVar', getTypoFreeVar);



